// src/pages/Home.js
import React from 'react';
import styled from 'styled-components';
import backgroundImage from '../assets/background.png'; // Ensure this path is correct
import Navbar from '../components/Navbar';

const HomeContainer = styled.div`
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const ContentBox = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 30px;
  border-radius: 10px;
  max-width: 600px;
  text-align: center;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
  }
`;

const Heading = styled.h1`
  color: #333;
`;

const Paragraph = styled.p`
  color: #666;
  font-size: 16px;
  line-height: 1.5;
`;

const Button = styled(Link)`
  display: inline-block;
  margin: 10px;
  padding: 10px 20px;
  background-color: #007bff;
  color: white;
  text-decoration: none;
  border-radius: 5px;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #0056b3;
  }
`;

function Home() {
  return (
    <div>
      <Navbar />
      <HomeContainer>
        <ContentBox>
          <Heading>Welcome to Car Pooling Ride Sharing</Heading>
          <Paragraph>
            This application is designed to help you share rides with others heading in the same direction. You can save on fuel costs, reduce your carbon footprint, and make new connections. Whether you're commuting to work, school, or just running errands, carpooling is a great way to travel. Join us today and start enjoying the benefits of carpooling.
          </Paragraph>
          <Button to="/signup">Sign Up</Button>
          <Button to="/login">Log In</Button>
          <Button to="/rides">Find Rides</Button>
          <Button to="/dashboard">Dashboard</Button>
          <Button to="/register">Register</Button>
        </ContentBox>
      </HomeContainer>
    </div>
  );
}

export default Home;
