import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import api from '../services/api';
import backgroundImage from '../assets/ride-search-background.jpg'; // Import the background image

const PageContainer = styled.div`
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContentContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const SearchBox = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  border-radius: 10px;
  max-width: 400px;
  margin-bottom: 20px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const LocationList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 10px 0;
`;

const LocationItem = styled.li`
  cursor: pointer;
  padding: 5px 10px;
  background-color: ${props => (props.selected ? '#007bff' : 'transparent')};
  color: ${props => (props.selected ? 'white' : '#333')};
  border-radius: 5px;
  margin: 5px;
`;

const RideSearch = () => {
  const history = useHistory();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState('');
  const [showLocationList, setShowLocationList] = useState(false); // State to control visibility of LocationList

  // Predefined locations
  const predefinedLocations = ['Mumbai', 'Delhi', 'Kolkata', 'Chennai', 'Bangalore'];

  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearchQuery(value);
    setShowLocationList(value.trim().length > 0); // Show location list if input has text
  };

  const handleLocationClick = (location) => {
    setSearchQuery(location);
    setSelectedLocation(location);
    setShowLocationList(false); // Hide location list after selection
  };

  const filterLocationsByAlphabet = (alphabet) => {
    return predefinedLocations.filter(location => location.toLowerCase().startsWith(alphabet.toLowerCase()));
  };

  const handleAlphabetClick = (alphabet) => {
    const filteredLocations = filterLocationsByAlphabet(alphabet);
    setSearchResults(filteredLocations);
  };

  const handleSearch = async () => {
    // Call backend API to search for rides
    try {
      const response = await api.get(`/rides?query=${searchQuery}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching for rides:', error);
    }
  };

  const handleRideClick = (rideId) => {
    // Redirect to ride details page
    history.push(`/ride/${rideId}`);
  };

  return (
    <PageContainer>
      <Navbar />
      <ContentContainer>
        <h2>Find Rides</h2>
        <SearchBox>
          <input
            type="text"
            placeholder="Enter Location"
            value={searchQuery}
            onChange={handleInputChange}
          />
          <button onClick={handleSearch}>Search</button>
          {showLocationList && (
            <LocationList>
              {Array.from('abcdefghijklmnopqrstuvwxyz').map((alphabet, index) => (
                <LocationItem
                  key={index}
                  onClick={() => handleAlphabetClick(alphabet)}
                >
                  {alphabet.toUpperCase()}
                </LocationItem>
              ))}
            </LocationList>
          )}
        </SearchBox>
        <ul>
          {searchResults.map((ride, index) => (
            <li key={index} onClick={() => handleRideClick(ride.id)}>
              {ride} - {ride.date}
            </li>
          ))}
        </ul>
      </ContentContainer>
    </PageContainer>
  );
};

export default RideSearch;
