// ShareMarketChart.js

import React from 'react';
import styled from 'styled-components';

const ChartContainer = styled.div`
  background-color: black; /* Black background */
  padding: 20px;
  width: 100%; /* Ensure the chart container spans the entire width */
  height: 100%; /* Ensure the chart container spans the entire height */
`;

const ChartHeading = styled.h2`
  color: yellow; /* Bright yellow color */
`;

const StyledSVG = styled.svg`
  width: 100%;
  height: 100%;
`;

function ShareMarketChart({ data, width, height }) {
  if (!data || data.length === 0) {
    return null; // Return null if data is undefined or empty
  }

  // Assume data is an array of { x, y } points
  const minX = Math.min(...data.map((point) => point.x));
  const maxX = Math.max(...data.map((point) => point.x));
  const minY = Math.min(...data.map((point) => point.y));
  const maxY = Math.max(...data.map((point) => point.y));

  // Calculate scaling factors for x and y axes
  const scaleX = width / (maxX - minX);
  const scaleY = height / (maxY - minY);

  // Generate path data for the line chart
  const pathData = data
    .map(({ x, y }) => `${(x - minX) * scaleX},${height - (y - minY) * scaleY}`)
    .join(' ');

  return (
    <ChartContainer>
      <ChartHeading>Share Market Chart</ChartHeading>
      <StyledSVG viewBox={`0 0 ${width} ${height}`}>
        <path d={`M ${pathData}`} />
        {/* Add axis labels and ticks */}
        {/* For example: */}
        {/* <text x="10" y={height - 10}>0</text> */}
        {/* <text x={width - 20} y={height - 10}>{maxX}</text> */}
      </StyledSVG>
    </ChartContainer>
  );
}

export default ShareMarketChart;
