import React, { useState } from 'react';
import RideDetails from './RideDetails'; // Adjust path as needed

function ParentComponent() {
  // Example state for selected card details
  const [selectedCard, setSelectedCard] = useState({
    cardNumber: '1234 5678 9101 1121',
    expiryDate: '12/24',
    cvv: '123'
  });

  // Function to update selected card details
  const handleCardSelection = () => {
    // Update selectedCard state based on user interaction or other logic
    setSelectedCard({
      cardNumber: '9876 5432 1098 7654',
      expiryDate: '11/23',
      cvv: '456'
    });
  };

  return (
    <div>
      <button onClick={handleCardSelection}>Select Card</button>
      {/* Pass selectedCard as prop to RideDetails */}
      <RideDetails selectedCard={selectedCard} />
    </div>
  );
}

export default ParentComponent;
