import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap'; // Import Bootstrap components
import styled from 'styled-components'; // Import styled-components for custom styling
import Navbar from '../components/Navbar';
import sedanImage from '../assets/sedan.jpg'; // Import car images
import swiftImage from '../assets/swift.jpg'; // Import car images

const PageContainer = styled.div`
  padding: 20px;
`;

const ContentContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const SearchBox = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 20px;

  input {
    width: 100%;
    padding: 10px;
    margin-bottom: 10px;
    border: 1px solid #ccc;
    border-radius: 5px;
    font-size: 16px;
  }

  button {
    padding: 10px 20px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
    transition: background-color 0.3s;

    &:hover {
      background-color: #0056b3;
    }
  }
`;

const LocationList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
  width: 100%;
  position: absolute;
  background-color: white;
  border: 1px solid #ccc;
  border-radius: 5px;
  z-index: 1;
`;

const LocationItem = styled.li`
  padding: 10px;
  cursor: pointer;
  background-color: ${props => (props.selected ? '#f0f0f0' : 'transparent')};

  &:hover {
    background-color: #f0f0f0;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 20px;
`;

const Car = styled.span`
  margin-left: 5px;
`;

const ResultsContainer = styled.div`
  display: grid;
  grid-gap: 20px;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
`;

const ResultContainer = styled.div`
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  cursor: pointer;
  transition: box-shadow 0.3s;

  &:hover {
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.2);
  }
`;

const CarImage = styled.img`
  width: 100%;
  border-radius: 5px;
  margin-bottom: 10px;
`;

const DriverDetails = styled.div`
  margin-top: 10px;
`;

const DriverContact = styled.p`
  font-size: 14px;
`;

const DriverRating = styled.p`
  font-size: 14px;
`;

const ShareCode = styled.p`
  font-size: 14px;
`;

const RideSearch = () => {
  const history = useHistory();
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showOriginList, setShowOriginList] = useState(false);
  const [showDestinationList, setShowDestinationList] = useState(false);
  const [loading, setLoading] = useState(false);
  const [carCount, setCarCount] = useState(1);

  const predefinedLocations = ['Mumbai', 'Delhi', 'Kolkata', 'Chennai', 'Bangalore', 'Goa', 'Dehradun'];

  const handleOriginInputChange = (e) => {
    const value = e.target.value.trim().toLowerCase();
    setOrigin(value);
    setShowOriginList(value.length > 0);
  };

  const handleDestinationInputChange = (e) => {
    const value = e.target.value.trim().toLowerCase();
    setDestination(value);
    setShowDestinationList(value.length > 0);
  };

  const handleLocationClick = (location, type) => {
    if (type === 'origin') {
      setOrigin(location);
      setShowOriginList(false);
    } else if (type === 'destination') {
      setDestination(location);
      setShowDestinationList(false);
    }
  };

  const handleSearch = () => {
    setLoading(true);

    let count = 1;
    const interval = setInterval(() => {
      setCarCount(count);
      count = count < 5 ? count + 1 : 1;
    }, 500);

    setTimeout(() => {
      clearInterval(interval);
      setLoading(false);

      setSearchResults([
        {
          id: 1,
          origin,
          destination,
          car: 'Sedan',
          carNumber: 'MH 12 AB 1234',
          driverName: 'John Doe',
          seatsAvailable: 3,
          driverContact: '+91 9876543210',
          driverRating: '⭐⭐⭐⭐⭐',
          shareCode: 'XYZ123'
        },
        {
          id: 2,
          origin,
          destination,
          car: 'Swift',
          carNumber: 'MH 14 CD 5678',
          driverName: 'Jane Doe',
          seatsAvailable: 2,
          driverContact: '+91 9876543211',
          driverRating: '⭐⭐⭐⭐',
          shareCode: 'ABC456'
        }
      ]);
    }, 5000);
  };

  const handleRideClick = (rideId) => {
    history.push(`/ride/${rideId}`);
  };

  return (
    <PageContainer>
      <Navbar />
      <ContentContainer>
        <h2>Find Rides</h2>
        <SearchBox>
          <input
            type="text"
            placeholder="Enter Origin Location"
            value={origin}
            onChange={handleOriginInputChange}
          />
          {showOriginList && (
            <LocationList>
              {predefinedLocations
                .filter(location => location.toLowerCase().startsWith(origin))
                .map((location) => (
                  <LocationItem
                    key={location}
                    selected={origin === location}
                    onClick={() => handleLocationClick(location, 'origin')}
                  >
                    {location}
                  </LocationItem>
                ))}
            </LocationList>
          )}
          <input
            type="text"
            placeholder="Enter Destination Location"
            value={destination}
            onChange={handleDestinationInputChange}
          />
          {showDestinationList && (
            <LocationList>
              {predefinedLocations
                .filter(location => location.toLowerCase().startsWith(destination))
                .map((location) => (
                  <LocationItem
                    key={location}
                    selected={destination === location}
                    onClick={() => handleLocationClick(location, 'destination')}
                  >
                    {location}
                  </LocationItem>
                ))}
            </LocationList>
          )}
          <button onClick={handleSearch}>Search</button>
          {loading && (
            <LoadingContainer>
              Please wait
              {Array.from({ length: carCount }).map((_, index) => (
                <Car key={index}>🚗</Car>
              ))}
            </LoadingContainer>
          )}
        </SearchBox>
        {!loading && searchResults.length > 0 && (
          <ResultsContainer>
            {searchResults.map((ride, index) => (
              <ResultContainer key={ride.id} onClick={() => handleRideClick(ride.id)}>
                <CarImage src={ride.car === 'Sedan' ? sedanImage : swiftImage} alt="Car" />
                <p>Car: {ride.car}</p>
                <p>Car Number: {ride.carNumber}</p>
                <p>Seats Available: {ride.seatsAvailable}</p>
                <DriverDetails>
                  <p>Driver Name: {ride.driverName}</p>
                  <DriverContact>Contact: {ride.driverContact}</DriverContact>
                  <DriverRating>Driver Rating: {ride.driverRating}</DriverRating>
                  <ShareCode>Share this code with the driver: {ride.shareCode}</ShareCode>
                </DriverDetails>
              </ResultContainer>
            ))}
          </ResultsContainer>
        )}
      </ContentContainer>
    </PageContainer>
  );
};

export default RideSearch;
