import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { Modal, Button, Form } from 'react-bootstrap'; // Import Bootstrap components

import Navbar from '../components/Navbar';
import sedanImage from '../assets/sedan.jpg'; // Import car images
import swiftImage from '../assets/swift.jpg'; // Import car images

const RideDetails = ({ ride }) => {
  const history = useHistory();
  const [showModal, setShowModal] = useState(false);
  const [paymentDetails, setPaymentDetails] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: ''
  });

  const handleCloseModal = () => setShowModal(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPaymentDetails(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleBooking = () => {
    // Process booking logic here (e.g., send booking details to backend)
    // For demo purposes, just show a confirmation alert
    alert('Booking successful! Receipt:\n' + JSON.stringify(paymentDetails, null, 2));
    handleCloseModal();
    // Optionally, you can redirect to a confirmation page after booking
    // history.push('/confirmation');
  };

  return (
    <div>
      <Navbar />
      <h2>Ride Details</h2>
      <div>
        <img src={ride.car === 'Sedan' ? sedanImage : swiftImage} alt="Car" style={{ width: '100px', height: '100px', borderRadius: '10px', marginBottom: '10px' }} />
        <p>Car: {ride.car}</p>
        <p>Car Number: {ride.carNumber}</p>
        <p>Seats Available: {ride.seatsAvailable}</p>
        <p>Driver Name: {ride.driverName}</p>
        <p>Contact: {ride.driverContact}</p>
        <p>Driver Rating: {ride.driverRating}</p>
        <p>Share this code with the driver: {ride.shareCode}</p>
      </div>
      <Button variant="primary" onClick={() => setShowModal(true)}>Book Now</Button>
      
      {/* Booking Modal */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Book {ride.car}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formCardNumber">
              <Form.Label>Card Number</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter card number"
                name="cardNumber"
                value={paymentDetails.cardNumber}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formExpiryDate">
              <Form.Label>Expiry Date</Form.Label>
              <Form.Control
                type="text"
                placeholder="MM/YY"
                name="expiryDate"
                value={paymentDetails.expiryDate}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group controlId="formCvv">
              <Form.Label>CVV</Form.Label>
              <Form.Control
                type="text"
                placeholder="CVV"
                name="cvv"
                value={paymentDetails.cvv}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" onClick={handleBooking}>
            Book Now
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default RideDetails;
