// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import backgroundImage from '../assets/background.png'; // Import the background image

const DashboardContainer = styled.div`
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

const ContentBox = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 30px;
  border-radius: 10px;
  max-width: 600px;
  text-align: center;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3);
  }
`;

const Heading = styled.h1`
  color: #333;
`;

const Paragraph = styled.p`
  color: #666;
  font-size: 16px;
  line-height: 1.5;
`;

const Button = styled.button`
  display: inline-block;
  margin: 10px;
  padding: 10px 20px;
  color: #fff;
  background-color: #007bff;
  border: none;
  border-radius: 5px;
  text-decoration: none;
  font-size: 16px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #0056b3;
  }
`;

function Dashboard() {
  const history = useHistory();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    } else {
      // Redirect to login page if user is not logged in
      history.push('/login');
    }
  }, [history]);

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    history.push('/login');
  };

  if (!user) {
    return null; // or loading indicator
  }

  return (
    <DashboardContainer>
      <Navbar />
      <ContentBox>
        <Heading>Welcome, {user.name}!</Heading>
        <Paragraph>Email: {user.email}</Paragraph>
        <Paragraph>
          Our Car Pooling Ride Sharing application offers various services to enhance your commuting experience. Enjoy affordable travel options by sharing rides with others heading in the same direction. Our platform helps you save on fuel costs, reduce your carbon footprint, and make new connections. Experience the convenience and benefits of carpooling with us.
        </Paragraph>
        <Button onClick={handleLogout}>Logout</Button>
      </ContentBox>
    </DashboardContainer>
  );
}

export default Dashboard;
