import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import Navbar from '../components/Navbar';
import backgroundImage from '../assets/ride-search-background.jpg'; // Import the background image
import sedanImage from '../assets/sedan.jpg'; // Import car images
import swiftImage from '../assets/swift.jpg'; // Import car images

const PageContainer = styled.div`
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContentContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const SearchBox = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  border-radius: 10px;
  max-width: 400px;
  margin-bottom: 20px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const LocationList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 10px 0;
`;

const LocationItem = styled.li`
  cursor: pointer;
  padding: 5px 10px;
  background-color: ${props => (props.selected ? '#007bff' : 'transparent')};
  color: ${props => (props.selected ? 'white' : '#333')};
  border-radius: 5px;
  margin: 5px;
`;

const LoadingContainer = styled.div`
  margin-top: 20px;
  font-size: 18px;
  color: #333;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const carAnimation = keyframes`
  0% { transform: translateX(0); }
  25% { transform: translateX(20px); }
  50% { transform: translateX(40px); }
  75% { transform: translateX(60px); }
  100% { transform: translateX(80px); }
`;

const Car = styled.span`
  display: inline-block;
  margin-left: 10px;
  animation: ${carAnimation} 0.5s linear infinite;
`;

const ResultsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px; /* Add space between sections */
`;

const ResultContainer = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  border-radius: 10px;
  max-width: 400px;
  margin: 10px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  transition: transform 0.3s ease, box-shadow 0.3s ease; /* Add transition for smooth hover effect */

  &:hover {
    transform: scale(1.05); /* Scale up on hover */
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3); /* Add shadow on hover */
  }
`;

const CarImage = styled.img`
  width: 100px;
  height: 100px;
  object-fit: cover;
  border-radius: 10px;
  margin-bottom: 10px;
`;

const DriverDetails = styled.div`
  margin-top: auto;
`;

const DriverContact = styled.p`
  margin-bottom: 5px;
`;

const DriverRating = styled.p`
  font-size: 18px;
  margin-bottom: 10px;
`;

const ShareCode = styled.p`
  font-size: 14px;
  margin-bottom: 5px;
`;

const Button = styled.button`
  background-color: #007bff;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #0056b3;
  }
`;

const ModalBackdrop = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContent = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 10px;
  max-width: 400px;
`;

const PaymentModal = ({ onClose, ride }) => {
  const [paymentMethod, setPaymentMethod] = useState('offline');
  const [cardNumber, setCardNumber] = useState('');

  const handlePaymentMethodChange = (method) => {
    setPaymentMethod(method);
  };

  const handleConfirmBooking = () => {
    // Implement your booking logic here (e.g., send booking request)
    alert('Ride booked successfully!');
    onClose(); // Close the modal after booking
  };

  return (
    <ModalBackdrop>
      <ModalContent>
        <h3>Select Payment Method</h3>
        <div>
          <label>
            <input
              type="radio"
              value="offline"
              checked={paymentMethod === 'offline'}
              onChange={() => handlePaymentMethodChange('offline')}
            />
            Offline (Cash)
          </label>
          <br />
          <label>
            <input
              type="radio"
              value="online"
              checked={paymentMethod === 'online'}
              onChange={() => handlePaymentMethodChange('online')}
            />
            Online (Card)
          </label>
          {paymentMethod === 'online' && (
            <div>
              <label>
                Card Number:
                <input
                  type="text"
                  value={cardNumber}
                  onChange={(e) => setCardNumber(e.target.value)}
                />
              </label>
            </div>
          )}
          <Button onClick={handleConfirmBooking}>Confirm</Button>
        </div>
      </ModalContent>
    </ModalBackdrop>
  );
};

const RideSearch = () => {
  const history = useHistory();
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showOriginList, setShowOriginList] = useState(false); // State to control visibility of Origin Location List
  const [showDestinationList, setShowDestinationList] = useState(false); // State to control visibility of Destination Location List
  const [loading, setLoading] = useState(false); // State to control loading
  const [carCount, setCarCount] = useState(1); // State to control the number of car emojis
  const [showPaymentModal, setShowPaymentModal] = useState(false); // State to control visibility of payment modal
  const [selectedRide, setSelectedRide] = useState(null); // State to hold the selected ride for booking

  // Predefined locations for origin and destination
  const predefinedLocations = ['Mumbai', 'Delhi', 'Kolkata', 'Chennai', 'Bangalore', 'Goa', 'Dehradun'];

  const handleOriginInputChange = (e) => {
    const value = e.target.value.trim().toLowerCase();
    setOrigin(value);
    setShowOriginList(value.length > 0); // Show origin location list if input has text
  };

  const handleDestinationInputChange = (e) => {
    const value = e.target.value.trim().toLowerCase();
    setDestination(value);
    setShowDestinationList(value.length > 0); // Show destination location list if input has text
  };

  const handleLocationClick = (location, type) => {
    if (type === 'origin') {
      setOrigin(location);
      setShowOriginList(false); // Hide origin location list after selection
    } else if (type === 'destination') {
      setDestination(location);
      setShowDestinationList(false); // Hide destination location list after selection
    }
  };

  const handleSearch = () => {
    setLoading(true); // Set loading state to true

    // Simulate animation of increasing and decreasing car emojis
    let count = 1;
    const interval = setInterval(() => {
      setCarCount(count);
      count = count < 5 ? count + 1 : 1;
    }, 500);

    // Simulate API call or data retrieval
    setTimeout(() => {
      clearInterval(interval);
      setLoading(false); // Set loading state to false after the search is complete

      // Manually set predefined search results
      setSearchResults([
        {
          id: 1,
          origin,
          destination,
          car: 'Sedan',
          carNumber: 'MH 12 AB 1234',
          driverName: 'John Doe',
          seatsAvailable: 3,
          driverContact: '+91 9876543210',
          driverRating: '⭐⭐⭐⭐⭐',
          shareCode: 'XYZ123'
        },
        {
          id: 2,
          origin,
          destination,
          car: 'Swift',
          carNumber: 'MH 14 CD 5678',
          driverName: 'Jane Doe',
          seatsAvailable: 2,
          driverContact: '+91 9876543211',
          driverRating: '⭐⭐⭐⭐',
          shareCode: 'ABC456'
        }
      ]);
    }, 5000); // 5 seconds delay
  };

  return (
    <PageContainer>
      <Navbar />
      <ContentContainer>
        <h2>Find Rides</h2>
        <SearchBox>
          <input
            type="text"
            placeholder="Enter Origin Location"
            value={origin}
            onChange={handleOriginInputChange}
          />
          {showOriginList && (
            <LocationList>
              {predefinedLocations
                .filter(location => location.toLowerCase().startsWith(origin))
                .map((location) => (
                  <LocationItem
                    key={location}
                    selected={origin === location}
                    onClick={() => handleLocationClick(location, 'origin')}
                  >
                    {location}
                  </LocationItem>
                ))}
            </LocationList>
          )}
          <input
            type="text"
            placeholder="Enter Destination Location"
            value={destination}
            onChange={handleDestinationInputChange}
          />
          {showDestinationList && (
            <LocationList>
              {predefinedLocations
                .filter(location => location.toLowerCase().startsWith(destination))
                .map((location) => (
                  <LocationItem
                    key={location}
                    selected={destination === location}
                    onClick={() => handleLocationClick(location, 'destination')}
                  >
                    {location}
                  </LocationItem>
                ))}
            </LocationList>
          )}
          <button onClick={handleSearch}>Search</button>
          {loading && (
            <LoadingContainer>
              Please wait
              {Array.from({ length: carCount }).map((_, index) => (
                <Car key={index}>🚗</Car>
              ))}
            </LoadingContainer>
          )}
        </SearchBox>
        {!loading && searchResults.length > 0 && (
          <ResultsContainer>
            {searchResults.map((ride, index) => (
              <ResultContainer key={ride.id}>
                <CarImage src={ride.car === 'Sedan' ? sedanImage : swiftImage} alt="Car" />
                <p>Car: {ride.car}</p>
                <p>Car Number: {ride.carNumber}</p>
                <p>Seats Available: {ride.seatsAvailable}</p>
                <DriverDetails>
                  <p>Driver Name: {ride.driverName}</p>
                  <DriverContact>Contact: {ride.driverContact}</DriverContact>
                  <DriverRating>Driver Rating: {ride.driverRating}</DriverRating>
                  <ShareCode>Share this code with the driver: {ride.shareCode}</ShareCode>
                </DriverDetails>
              </ResultContainer>
            ))}
          </ResultsContainer>
        )}
      </ContentContainer>
    </PageContainer>
  );  
};
export default RideSearch;
