import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import Navbar from '../components/Navbar';
import api from '../services/api';
import backgroundImage from '../assets/ride-search-background.jpg'; // Import the background image

const PageContainer = styled.div`
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const ContentContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const SearchBox = styled.div`
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  border-radius: 10px;
  max-width: 400px;
  margin-bottom: 20px;
  box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const RideSearch = () => {
  const history = useHistory();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearch = async () => {
    // Call backend API to search for rides
    try {
      const response = await api.get(`/rides?query=${searchQuery}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error searching for rides:', error);
    }
  };

  const handleRideClick = (rideId) => {
    // Redirect to ride details page
    history.push(`/ride/${rideId}`);
  };

  return (
    <PageContainer>
      <Navbar />
      <ContentContainer>
        <h2>Find Rides</h2>
        <SearchBox>
          <input type="text" placeholder="Enter Location" value={searchQuery} onChange={handleInputChange} />
          <button onClick={handleSearch}>Search</button>
        </SearchBox>
        <ul>
          {searchResults.map(ride => (
            <li key={ride.id} onClick={() => handleRideClick(ride.id)}>
              {ride.origin} to {ride.destination} - {ride.date}
            </li>
          ))}
        </ul>
      </ContentContainer>
    </PageContainer>
  );
}

export default RideSearch;
